import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Add this line
import './Loginpage.css';

export default function LoginPage() {
  const [role, setRole] = useState('patient');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const navigate = useNavigate(); // Hook for navigation

  const handleLogin = async () => {
    const response = await fetch('/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password, role }),
    });

    const data = await response.json();

    if (data.success) {
      alert('Login successful!');
      // Navigate based on role
      if (role === 'doctor') {
        navigate('/diagnostic'); // Assuming this is the route for AIDiagnosticForm
      } else {
        navigate('/home'); // For patients
      }
    } else {
      alert('Login failed');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Welcome to Oncolink!</h1>
        <p className="sub">Log in to your account</p>

        <div className="role-selector">
          <label>
            <input
              type="radio"
              name="role"
              value="doctor"
              checked={role === 'doctor'}
              onChange={() => setRole('doctor')}
            />
            Doctor
          </label>
          <label>
            <input
              type="radio"
              name="role"
              value="patient"
              checked={role === 'patient'}
              onChange={() => setRole('patient')}
            />
            Patient
          </label>
        </div>

        <label>Username</label>
        <input
          type="text"
          placeholder="Email or Phone Number"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Password</label>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <div className="options">
          <label>
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
            />
            Remember me
          </label>
          <span className="reset">Reset Password?</span>
        </div>

        <button className="login-button" onClick={handleLogin}>
          Sign In
        </button>

        <p className="register">
          Don't have an account yet? <a href="/signup">Register here</a>
        </p>
      </div>
    </div>
  );
}
